(338, 'Cerimônia de Comemoração da Semana Florestal 2013', 'cerimonia-de-comemoracao-da-semana-florestal-2013', '', '2013-09-23', NULL, '2013-09-23', NULL, 'Parque Estadual da Serra do Rola Moça - Belo Horizonte/ MG', '', 1606, '', 0, '<p>O governador de Minas Gerais, Antonio Anastasia, e o secretário de estado de Meio Ambiente e Desenvolvimento Sustentável, Adriano Magalhães, convidam para a cerimônia de comemoração da Semana Florestal 2013.<br></p>', 1, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', 499, '0000-00-00 00:00:00', 0, 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
