(1, 61, 'Cigma 11º Encontro Nacional de Vereadores', 'cigma-11-encontro-nacional-de-vereadores', '<p><img src=\"images/blog/publicacoes/fotos_11072019.jpg\" alt=\"\" width=\"400\" height=\"400\" /> <img src=\"images/blog/publicacoes/fotos_11072019_2.jpg\" alt=\"\" width=\"400\" height=\"400\" /> <img src=\"images/blog/publicacoes/fotos_11072019_3.jpg\" alt=\"\" width=\"400\" height=\"400\" /></p>\r\n<p><img src=\"images/blog/publicacoes/fotos_11072019_4.jpg\" alt=\"\" width=\"400\" height=\"400\" /> <img src=\"images/blog/publicacoes/fotos_11072019_5.jpg\" alt=\"\" width=\"400\" height=\"400\" /></p>', '', 1, 8, '2019-07-09 20:26:41', 162, '', '2019-08-20 21:48:35', 162, 0, '0000-00-00 00:00:00', '2019-07-09 20:26:41', '0000-00-00 00:00:00', '{\"image_intro\":\"\",\"float_intro\":\"\",\"image_intro_alt\":\"\",\"image_intro_caption\":\"\",\"image_fulltext\":\"\",\"float_fulltext\":\"\",\"image_fulltext_alt\":\"\",\"image_fulltext_caption\":\"\"}', '{\"urla\":false,\"urlatext\":\"\",\"targeta\":\"\",\"urlb\":false,\"urlbtext\":\"\",\"targetb\":\"\",\"urlc\":false,\"urlctext\":\"\",\"targetc\":\"\"}', '{\"article_layout\":\"\",\"show_title\":\"\",\"link_titles\":\"\",\"show_tags\":\"\",\"show_intro\":\"\",\"info_block_position\":\"\",\"info_block_show_title\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_associations\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_page_title\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', 3, 2, '', '', 1, 269, '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"